//***********************************************************************************************************
// handler for all events regarding button-fields ( menu and more ) 
// called by buttons-function by: buttonHandler(event.target.name);
//***********************************************************************************************************
function buttonHandler( ButtonName )
{
	markedDirty = this.dirty;	// save the current dirty-status
	switch ( ButtonName )
	{
		case "Btn_CloseDoc":	// close document button action ( upper right corner )
			{
				if (this.external == false) this.closeDoc();
			}	break;
		case "Btn_FullScreen":	// Button to switch to full-screen or normal-screen ( upper right corner )
			{
				if (this.external == false) if ( app.fs.isFullScreen == true ) app.fs.isFullScreen = false; else app.fs.isFullScreen = true;
			}	break;
		case "Btn_InfoAbout":	// Info-, version and history dialog button "?" ( upper right corner )
			{
				Dlg_VersionHistory();
				this.dirty = markedDirty;
			}	break;
		case "Btn_SubmitCRM":	// CRM submit order-list button ( visible, if doc opened in browser )
			{
				this.getField("Transmitter").value = "";	// clear the field-value of Transmitter-field
				if ( String(Library.VisiblePage.toString()) != "" )	transmitToCRMSystem(); // if finalisation is done, transmit the data, else show warning message do Finalise first before transmitting
				else app.alert({cMsg: STR("PleaseFinalise","Please finalise the selection first!","dc") ,cTitle: STR("NotPossible","Function not possible","dc"), nIcon: 1, nType: 0}); 
				this.getField("Transmitter").value = "";	// clear off transmitted information
				this.dirty = markedDirty;	// set last dirty status as submitting does not change the document
			}	break;

		case "Btn_Reset":	// Menu-button to reset the whole document
			{
				markedDirty = true;	// set dirty to "changed"
				resetAll();
				for each ( var i in Registry.System.Logic.FunctionGroups.Choice.children() ) i.@items="";	// clear the single or multiple choice items
				Library_Init();
				Registry.Internals.settings.finalised.@value = false;
				registrySave();
			}	break;
		case "Btn_SetLanguage":
			{
				markedDirty = true;	// set dirty to "changed"
				var currentLanguage = Library.CurrentLanguage.@name;
			var languageList = {};
				var languageLCID = {};
				var languageListText = {};
				var s="";
				for ( var x =0; x<Library.elements().length(); x++ ) if ( String(Library.elements()[x].name()) == "Language" ) 
				{
					languageList[Library.elements()[x].@text] = Library.elements()[x].@name;
					languageLCID[Library.elements()[x].@text] = Library.elements()[x].@lcid;
					languageListText[Library.elements()[x].@text] = -1; 
					if ( String(Library.elements()[x].@name) == currentLanguage ) currentLanguage = Library.elements()[x].@text;
				}
				var choice = Dlg_SelectListItem ( STR("dlgSetLangDlgCaption","Select the document language","dc")+"", languageListText, currentLanguage, 400, 200 );
				if ( choice != "" )
				{
					for ( var x in languageList ) if ( choice == x )
					{
						Library.CurrentLanguage.@name = languageList[x]+"";
						Library.CurrentLanguage.@lcid = languageLCID[x]+"";
						Library.CurrentLanguage.@text = x;
						Library_Save();
					}
					Library_Init();
					setBottomBarInfoLine();
					createDynamicData();	// create dynamic calculated data-set
					if ( String(Library.VisiblePage)+"" != "" ) showPage ( "@"+Library.VisiblePage, true );
				}
			}	break;
		case "Btn_Finalise":	// Finalise with selection-dialog of the results page (hyperpage)
			{
				
				markedDirty = true;	// set dirty-marker to "changed"
				var hyperPageList = {};	// hyperpage-list for selection dialog
				var currentPage = "";	// displayed name of the current visible page
				for ( var x in PageList )	// create hyperpage-list-copy from PageList
				{
					hyperPageList[x] = PageList[x];	// make copy of all PageList-values
					if ( PageList[x] == Library.VisiblePage ) currentPage = x;	// compare if page entry is currently visible page
				};
				var selection = Dlg_SelectListItem ( STR("dlgSelHyperPageDlgCaption","Select information list type","dc")+"", hyperPageList, currentPage, 400, 200 );	// start hyperpage-selection-dialog
				if ( selection != "" ) if ( ownerId.toString() == "28653030" ) // if selection is done - no cancel pressed
				{
					this.getField("LogicChart").display = display.hidden;	// hide the logic-chart-image
					finaliseActionLogic();	// remove all remaining Sprites from selection-area
					createDynamicData();	// create dynamic calculated data-set
					for ( var x in PageList ) if ( selection == x )	// search for hyperpage (@name) related to the selection-displayed name (@text)
					{
						showPage ( x, true );	// generate and show the seleted page by @name
						Library.VisiblePage = PageList[x]; Library_Save();	// overwrite the VisiblePage-value in the Library
					}
				}
			}	break;
		case "Btn_FinalisePhysicals":
			{
				markedDirty = true;	// set dirty to "changed"
				this.getField("LogicChart").display = display.hidden;
				finaliseActionLogic();
				var pageName = "Physicals";
				createDynamicData();														// create dynamic calculated data-set
				showPage ( "@"+pageName, true ); Library.VisiblePage = pageName; Library_Save();
			}	break;
		case "Btn_Back": 
			{
				markedDirty = true;															// set dirty to "changed"
				var cfgList = Registry.System.Logic.Clicked.split(";");  					// get the list of clicked icons ( no added items!! )
				var cfgLength = cfgList.length;
				if ( String(Library.VisiblePage.toString()) == "" ) cfgLength--;					// if finalisation is not done ( still in selection mode )
				resetAll(); 																// reset the selection
				Library_Init();																// re-init the library to reload icon's usernames
				backSelection = true;														// enable re-click mode
				for ( var x = 0; x<cfgLength; x++ ) iconHandler(cfgList[x]);				// virtually re-click the listed icons except the last one
				backSelection = false;														// disable re-click mode
				
				Registry.Internals.settings.finalised.@value = false;
				registrySave();				

			}	break;
		case "Btn_RunAttachement":
			{
				var d=this.dataObjects;
				var attList = {};
				for ( var i=0; i<d.length; i++ )
				{
					var vName = d[i].description; if ( vName == "" ) vName = d[i].name;
					attList[(i+1)+":  "+vName] = -1;
				}
				var selection = Dlg_SelectListItem ( STR("dlgSelAttachementDlgCaption","Open file attached to this Foliant","dc")+"", attList, "", 500, 300 );
				if ( selection != "" )
				{
					var attNum = parseInt(selection)-1;
					this.exportDataObject({ cName: d[attNum].name, nLaunch: 2 });					
				}
			}	break;
		case "Btn_SelectScreen": 
			{
				markedDirty = true;	// set dirty to "changed"
				var f = this.getField("Screen");
				if (f.display == display.visible)
				{
					switch ( f.buttonPosition )
					{
						case (position.textOnly): { f.buttonPosition = position.iconOnly; f.fillColor = ["T"]; } break;
						case (position.iconOnly): { f.display = display.hidden;} break;
					}
				} 
				else
				{
					f.buttonPosition = position.textOnly;
					f.fillColor = colorStrToColor( Registry.Internals.colors.screenBG.@value );
					f.display = display.visible;
				}
			}	break;
	}
	arrangeMenuButtons();
	this.dirty = markedDirty; // set the dirty-value to the marker's value
}

//**********************************************************************************************************************
// handler for all buttons at MouseOut-event
//**********************************************************************************************************************
function buttonMouseOutHandler( btnName )	
{
	markedDirty = this.dirty;	// save the current dirty-status
	this.getField( btnName ).fillColor = ["T"];
	this.dirty = markedDirty; // set the dirty-value to the marker's value
}

//**********************************************************************************************************************
// handler for all buttons at MouseIn-event
//**********************************************************************************************************************
function buttonMouseInHandler( btnName )
{
	markedDirty = this.dirty;	// save the current dirty-status
	this.getField( btnName ).fillColor =  colorStrToColor( Registry.Internals.colors.buttonBG.@value );
	this.dirty = markedDirty; // set the dirty-value to the marker's value
}

//**********************************************************************************************************************
// set language depending hint text of buttons and sprites (  mouse-over hint )
//**********************************************************************************************************************
function setHintsAndCaptions()
{
	var childIdx = Library.Language.(@name == Library.CurrentLanguage).childIndex();
	var xList = Library.children()[childIdx].elements().(@type=="sh");
	for each ( var sh in xList ) {	var f = this.getField( sh.@name ); if ( f != null ) f.userName = sh.@text }
	var xList = Library.children()[childIdx].elements().(@type=="un");
	for each ( var un in xList ) {	var f = this.getField( un.@name ); if ( f != null ) f.userName = un.@text }
	var xList = Library.children()[childIdx].elements().(@type=="bc");
	for each ( var bc in xList ) {	var f = this.getField( bc.@name ); if ( f != null ) f.buttonSetCaption( bc.@text ) }
}


//**********************************************************************************************************************
// set position and status of the menu-buttons regarding the visible-settings in the registry
//**********************************************************************************************************************
function arrangeMenuButtons()
{
	Registry.Internals.menuItems.child("Btn_RunAttachement").@visible = true; 
	if (this.dataObjects == null ) Registry.Internals.menuItems.child("Btn_RunAttachement").@visible = false;	// hide the Attachement-button if no attachement available

	var barRect = this.getField("bar_CenterLine").rect;	// get the position and size of the menu-background-bar
	var visBtnNum = 0;	// number of visible menu-buttons
	var visBtnWidth = 0;	// total width of visible menu-buttons
	for ( var x = 0; x<Registry.Internals.menuItems.children().length(); x++ ) 
	{
		var f = this.getField(Registry.Internals.menuItems.children()[x].name());	// get the button
		if ( Registry.Internals.menuItems.children()[x].@visible == true )	// is the button marked as visible ?
		{
			f.display = display.visible;	// make button visible - if not yet
			visBtnNum++; // count the number of visible menu-buttons
			visBtnWidth = f.rect[2] - f.rect[0];	// remember the last visible button's width
		}
		else f.display = display.hidden;	// make button invisible
	}
	var btnOffset = ( ( barRect[2] - barRect[0] ) - visBtnWidth ) / ( visBtnNum-1 );
	var btnCount = 0;
	if ( visBtnNum>=2 ) for ( var x = 0; x<Registry.Internals.menuItems.children().length(); x++ ) 
	{
		var f = this.getField(Registry.Internals.menuItems.children()[x].name());	// get the button
		if ( f.display==display.visible )
		{
			var left = barRect[0];
			var right = left+f.rect[2]-f.rect[0];
			f.rect = [left+btnCount*btnOffset,f.rect[1],right+btnCount*btnOffset,f.rect[3] ];
			btnCount++;
		}
	}
}

//**********************************************************************************************************************
// reset the whole document structure / variables, registry, library, fields ...
//**********************************************************************************************************************
function resetAll()
{
	resetIconStages ();
	resetActionLogic();
	//this.getField("InfoBoard").display = display.hidden;  // not working in Reader !!!
	//this.getField("InfoBoard").rect = [0,0,0,0];
	this.getField("LogicChart").display = display.visible;
	this.getField("InfoBoard").value = "";
	this.getField("InfoBoard").readonly = true;
	Library.VisiblePage = ""; 
	Library_Save();
}

