//************************************************************************************************************************
//***  Dialog with two listboxes, ok- and cancel-button, variable caption, default item selection and selectable dimension
//***  return-value is a selected item's list from the original item-list ( displayed string )
//***  var selection = Dlg_SelectListItem ( "My dialog", ["test","hello"], "hello", 300, 200 );
//************************************************************************************************************************
function Dlg_SelectMultipleListItem ( caption, itemList, defaultItem, dlgWidth, dlgHeight )
{
	var result = "";

	var DlgSelectMultipleListItem =
	{
		DoDialog: function(){return app.execDialog(this);},
		initialize: function(dialog)
		{
			dialog.load({"capt":caption});
			dialog.load({"lstA":itemList});
			var e=false; for ( var x in itemList ) e=true; dialog.enable({"btnA":e});	// enable button "add" only, if items available to add
			dialog.enable({"btnR":false});	// disable button "remove"
		},
		"btnA": function(dialog)	// Button "add" to selection list
		{
			var listA = dialog.store()["lstA"];	// get the available-list items
			var listS = dialog.store()["lstS"];	// get the selection-list items
			for ( var x in listS ) listS[x]=-1;	// unmark all items in selection-list
			var nl = []; for ( var x in listA ) nl.push(x); nl.sort();	// create sorted list of items
			var nextSel = -1;	// define next-selection variable
			for ( var x in nl ) if ( listA[nl[x]] >= 0 ) 	// run over all items in list and search for the selected item
			{ 
				listS[nl[x]] = 1;	// transfer the selection to "selected" list
				delete listA[nl[x]];	// remove selection from "available" listbox
				nl[x]=""; nl.sort(); nl.shift();	// remove selection from name-list
				if ( x>=nl.length ) nextSel=nl.length-1; else nextSel = x;	// set nextSel to correct position
				x= nl.length;	// force leaving the for-loop
			};
			if ( nextSel>=0 ) listA[nl[nextSel]] = 1;	// set the new seletion of available items
			dialog.load({"lstA":listA});	// re-load the "available"-list
			dialog.load({"lstS":listS});	// re-load the "selected"-list
			var e=false; for ( var x in listA ) e=true; dialog.enable({"btnA":e});	// enable button "add" only, if items available to add
			var e=false; for ( var x in listS ) e=true; dialog.enable({"btnR":e});	// enable button "remove" only, if items available to add
		},
		"btnR": function(dialog)	// Button "remove" from selection list
		{
			var listA = dialog.store()["lstA"];	// get the available-list items
			var listS = dialog.store()["lstS"];	// get the selection-list items
			for ( var x in listA ) listA[x]=-1;	// unmark all items in selection-list
			var nl = []; for ( var x in listS ) nl.push(x); nl.sort();	// create sorted list of items
			var nextSel = -1;	// define next-selection variable
			for ( var x in nl ) if ( listS[nl[x]] >= 0 ) 	// run over all items in list and search for the selected item
			{ 
				listA[nl[x]] = 1;	// transfer the selection to "selected" list
				delete listS[nl[x]];	// remove selection from "available" listbox
				nl[x]=""; nl.sort(); nl.shift();	// remove selection from name-list
				if ( x>=nl.length ) nextSel=nl.length-1; else nextSel = x;	// set nextSel to correct position
				x= nl.length;	// force leaving the for-loop
			};
			if ( nextSel>=0 ) listS[nl[nextSel]] = 1;	// set the new seletion of available items
			dialog.load({"lstA":listA});	// re-load the "available"-list
			dialog.load({"lstS":listS});	// re-load the "selected"-list
			var e=false; for ( var x in listA ) e=true; dialog.enable({"btnA":e});	// enable button "add" only, if items available to add
			var e=false; for ( var x in listS ) e=true; dialog.enable({"btnR":e});	// enable button "remove" only, if items available to add
		},
		"ok": function(dialog)
		{
			var listS = dialog.store()["lstS"];
			var tmp = [];
			for ( var x in listS ) tmp.push(x);
			result = tmp.join(";");
		},
		description:
		{name: "Select multiple items",elements:[
		{type: "view",elements:[
		{type: "static_text",item_id: "capt",name: "Select multiple items from the list below",char_width: 30,alignment: "align_center",font: "dialog",bold: true,},
		{type: "view",align_children: "align_distribute",elements:[
		{type: "cluster",item_id: "clsA",name: STR("dlgCaptAvailableItems","Available items","dc"),width: 280,height: 300,align_children: "align_center",elements:[
		{type: "list_box",item_id: "lstA",width: dlgWidth,height: dlgHeight,},
		{type: "button",item_id: "btnA",name: STR("dlgBtnAddListItem","Add selected item","dc"),height: 30,alignment: "align_fill",},]},
		{type: "cluster",item_id: "clsS",name: STR("dlgCaptSelectedItems","Selected items","dc"),width: 280,height: 300,align_children: "align_center",elements:[
		{type: "list_box",item_id: "lstS",width: dlgWidth,height: dlgHeight,},
		{type: "button",item_id: "btnR",name: STR("dlgBtnRemoveListItem","Remove item","dc"),height: 30,alignment: "align_fill",},]},]},
		{type: "ok_cancel",width: 80,height: 32,ok_name: STR("dlgBtnOk","Ok","dc"),cancel_name: STR("dlgBtnCancel","Cancel","dc"),},]},]}
	};

	if("ok" == DlgSelectMultipleListItem.DoDialog()){}
	return result;
}




//************************************************************************************************************************
//***  Capere dialog function logic trainer
//************************************************************************************************************************
function capere()
{

	var col = "";
	var status = [0,0,1,0,1,0,1,0,0];
	var clicks = 0;
	var finish = 0;
	var totalClicks = 0;
	var games = 1;

	function getFillBytes ( maxCount, currCount ){	var dat = col;	while ( dat.length < maxCount-currCount ) dat += dat;	return dat.substr(0,maxCount-currCount); } 
	function swap( num ) { status[num] = [0,1,0][status[num]+1] }
	function randomize() { do { for ( var x=0; x<9; x++ ) status[x]= parseInt( Math.round( Math.random() ) );} while ( ( status.join("") ==  "111111111") || ( status.join("") == "000000000" ) );}

	var JSADMDlg1 =
	{
		result:"cancel",
		clickNum:0,
		DoDialog: function(){return app.execDialog(this);},

		setColors: function( dialog )
		{
			for ( var x=1; x<=9; x++ )
			{
				if ( status[ x-1 ] == 0 ) col = "FFAACC00"; else col = "FF1144AA";
				var iData = {};	iData["img"+x] = { "width": 100, "height": 100, offset: 0, "read": function( count ) { var s = getFillBytes ( this.width*this.height*8, this.offset ); this.offset += s.length; return s } };
				dialog.load( iData );
			}
			dialog.load({"labM":"Games: "+games+"  Finished: "+finish+"  Clicks: "+totalClicks+"  Step: "+clicks+"/10"});
		},

		initialize: function( dialog ){	randomize(); this.setColors( dialog ); },
		clicked: function( dialog, iNum)
		{
			switch (iNum)
			{
				case 0: { swap(0);swap(1);swap(4);swap(3) } break;
				case 1: { swap(0);swap(1);swap(2);swap(3);swap(4);swap(5) } break;
				case 2: { swap(1);swap(2);swap(4);swap(5) } break;
				case 3: { swap(0);swap(1);swap(3);swap(4);swap(6);swap(7) } break;
				case 4: { swap(4) } break;
				case 5: { swap(1);swap(2);swap(4);swap(5);swap(7);swap(8) } break;
				case 6: { swap(3);swap(4);swap(6);swap(7) } break;
