var dynamicData = {};	// Dynamic data list-array

//*********************************************************************************
//********** Dynamic value text-processor function ********************************
//*********************************************************************************
function DYN( valName )
{
	if ( dynamicData[valName] == null ) return 'DYN("'+valName+'")';	// if value does not exist, return requested instruction as string
	return dynamicData[valName].toString();	// return dynamicData-value
}

//*********************************************************************************
//********** Create dynamic value from given value-set-table calculation string ***
//*********************************************************************************
function createDynamicDataFromValueSet( valueSet )
{
	for each ( var v in valueSet.children() )	// run over all children of a given valueSet-table
	{
		var vName = v.name().toString();	// create dynamicData-name-entry
		TABLOAD(v.@table.toString());	// load related table
		try
		{
			eval( "dynamicData[vName]="+v.@code.toString() );	// execute given calculation string and store result in dynamicData
		}
		catch(e)
		{
			app.alert("Dynamic Data error in: " + vName +'\rCode: '+v.@code.toString());	// show error message with code information
			dynamicData[vName]="!!DYN-error:"+v.@code.toString()+"!!";	// set dynamic-value with errormessage text
		}
	}
}

//*********************************************************************************
//********** Clear the dynamic value's from list-array ****************************
//*********************************************************************************
function clearDynamicData()
{
	dynamicData = {};
}

//*********************************************************************************
//********** Parse through dynamic value sets and create data *********************
//*********************************************************************************
function createDynamicData()
{
	clearDynamicData();	// empty the dynamicData-list
	dynamicData.languageName = currentLanguageName;	//	set languageName (english name ) for initialisation
	dynamicData.languageText = currentLanguageText;	//	set languageText (local language name) for initialisation
	var pXML = new XML ( this.getField("Pandora").value );	// get the XML-structure from Pandora
	var valSet = pXML.DynamicData.ValueSets.ValueSet.(@lcid=="-1" );	// get the default valueSet (lcid=-1)
	createDynamicDataFromValueSet( valSet );	// create default dynamicData list-values
	for each ( var valSet in pXML.DynamicData.ValueSets.children() )	// run over all valueSets
	{
		var lcidArray = valSet.@lcid.toString().split(";");	// get valueSets lcid-list as string array
		if ( lcidArray.indexOf(lcidInUse+"")>=0 ) createDynamicDataFromValueSet( valSet );	// create dynamicData-values if current language lcid matches the valueSet
	}
}

