// ************ handle clicked icon actions ***************************************************
function iconHandler( IconName )
{
	if ( Registry.System.Logic.Enabled.toString().indexOf(IconName+';') < 0 ) return;	// return, if icon not enabled
	if ( Registry.System.Atoms.Item.(@name==IconName).Stage.toString() == Registry.System.Atoms.Item.(@name==IconName).ZeroStage.toString() )	// if icon currently in ZeroStage
	{
		if ( checkIconChoice( IconName ) != true ) return;
	}
	var s = String(Registry.System.Logic.Clicked); if ( s != "" ) s+=";"; s+=IconName; Registry.System.Logic.Clicked = s; 
	do 
	{
		if ( this.getField(IconName) != null )
		{
			var iNum = Registry.System.Atoms.Item.(@name==IconName).childIndex();
			var currentStage = Registry.System.Atoms.Item[iNum].Stage.toString();
			var resetStage = Registry.System.Atoms.Item[iNum].ZeroStage.toString();
			var nextStage = Registry.System.Atoms.Item[iNum].Stages.elements(currentStage).nextStage;
			
			if (Registry.System.Logic.Enabled.toString().indexOf(IconName+';')>=0)
			{
				setIconToStage( IconName, nextStage );
				runActionLogic( IconName );
				checkIconGrouping();
				markedDirty = true;
				this.dirty = markedDirty; // set the dirty-value to the marker's value
			}
			if ( String(Registry.System.Logic.Active) == "false") setIconToStage( IconName, nextStage );
		}
		IconName = "";
		if ( IconToAdd != "" )
		{
			var iconList = IconToAdd.split(";");
			IconName = iconList.shift();
			IconToAdd = iconList.join(";");
		} 
	} 
	while ( IconName != "" );	// repeat, while IconName not empty ( still icons to be added )
	registrySave();
}


// ************ check icon action for multiple items-selection ***************************************************
function checkIconChoice( IconName )
{
	var choiceMembers = Registry.System.Logic.FunctionGroups.Choice.child(IconName).toString().split(";"); 	// get the icon's choice members list
	if ( choiceMembers.join("") == "" ) return true;	// if no members available, leave
	
	var filter = String( Registry.System.Logic.FunctionGroups.Choice.child( IconName ).@filter.toString()).toUpperCase();	// get the item-list filter of the choice
	if ( filter  == "EN" )	// filter the list of items depending on related sprite's ENABLED-status
	{	
		var choiceMembersTmp = [];
		var enA = String(Registry.System.Logic.Enabled.toString()).split(";");		// get "Enabled"-list as array
		for each ( var cm in choiceMembers )	// run over all entries defined for choice 
		{
			var cmA = cm.split(":");	// separate the current handled entry into tags and sprites in an array
			var cEnabled = true;	// basic entry visible indicator
			for each ( var tagSprite in cmA )	// run over all items in the current choice entry
				if (this.getField(tagSprite) != null)	// if item is a sprite ...
				{
					if (enA.indexOf(tagSprite) < 0) 
					{
						cEnabled = false;	// if sprite is not enabled, set basic entry visible indicator false
						break;	
					}
				}
			if ( cEnabled ) choiceMembersTmp.push(cm);	// if all related icons enabled, save this choice item
		}
		choiceMembers=[];	// cleanup the original choice-item-list
		for each ( var cm in choiceMembersTmp ) choiceMembers.push(cm);	// restore only choice-items with enabled icons
	}
	
	
	
	var sn = this.getField(IconName).buttonGetCaption();	// get the icon's name for dialog caption
	var sx = "";
	var ex = {}; 
	var tagActionSpriteList = {};
	for each ( s in choiceMembers )	// fill listobject with description=choiceMembers values
	{
		var dscr = "Dscr"+s.split(":")[0];
		tagActionSpriteList[s.split(":")[0]] = s.split(":");
		sx = STR( dscr ); if ( sx.indexOf("??")>= 0 ) sx = STR( dscr, s.split(":")[0] + ": ############### missing description for:" + s.split(":")[0] );
		ex[sx]=s;
	}
	var selection = "";
	var count = parseInt( Registry.System.Logic.FunctionGroups.Choice.child( IconName ).@count.toString() );	// get the item-count-value of the choice
	if ( count == 1 )	// for single choice item call simple list-dialog
	{
		if ( backSelection == false )	// if not in reverse selection mode (remove last item function)
		{
			selection = Dlg_SelectListItem ( STR("dlgChoiceCaption","Select one of the following items in","dc")+" '"+sn+"'", ex, "", 450, 200 );	// start selection dialog
			if ( selection == "" ) return false;	// if no selection done, return empty
		}
		else for ( var s in ex )	// if in back-selection mode, run over all selectable items
		{
			if ( ex[s].split(":")[0] == Registry.System.Logic.FunctionGroups.Choice.child( IconName ).@items.split(":")[0] ) selection = s;	// restore selection value from items-list
		}
	}
	else  // ########################################################################################################################################################################################################################
	{
		if ( backSelection == false )	// ... in normal selection mode
		{
			selection = Dlg_SelectMultipleListItem ( STR("dlgMultipleChoiceCaption","Select one or more of the following items in","dc")+" '"+sn+"'", ex, "", 290, 240 );
			if ( selection == "" ) return false;	// if non selected or cancel pressed, return empty
		}
		else	// in back-selection mode
		{
			var sel = [];	// create a selection array
			for ( var s in ex )	// run over all selectable choices
				if ( Registry.System.Logic.FunctionGroups.Choice.child( IconName ).@items.split(";").indexOf(ex[s]) >= 0 ) sel.push( s);	// get each selected choice
			selection = sel.join(";");	// make a selection-list with ";" separators
		}
	}
	
	IconToAdd = "";	// reset the string of icons to add
	var selItemsA = selection.split(";");	// separate the selected items in an array
	for each ( var sia in selItemsA )	// run over all separated, selected items
	{
		var addIconFromTagList = ex[sia].split(":");	// separate the choice and sprites to add
		addIconFromTagList.shift();	// remove the choice from list array
		if (addIconFromTagList.length>0)
		{
			IconToAdd += addIconFromTagList.join(";")+";";	// add the remaining sprites to the add-list
		}
	}
	while ( IconToAdd.indexOf(";;")>=0 ) IconToAdd = IconToAdd.replace(";;",";");	// remove bad list-signs
	
	if ( selection != "" )	// if single or multiple selection done ...
	{
		var subLines = "";	// reset the description-sublines
		var selTagList = [];	// empty the selected tags list
		var selArray = selection.split(";");	// create array of selected items
		for each ( var s in selArray )	// run over all selection-items in array
		{
			subLines+="\r - "+s.split(":")[0];	// create the sub-text-lines from choice only ([0])
			selTagList.push( ex[s]);	// add the tag to taglist
		}
		Registry.System.Logic.FunctionGroups.Choice.child( IconName ).@items = selTagList.join(";");	// store the selected items in the choice-@items-attribute
		this.getField(IconName).userName = this.getField(IconName).userName.split("\r")[0] + subLines;	// add the sublines to the username-string of the icon
	} else return false;
	return true;
}
 
// ************ check icon for displaying in a grouping-line ***************************************************
function checkIconGrouping()
{
	var Selection = Registry.System.Logic.Selection.toString().split(';');
	var NumGroups = Registry.System.Logic.Groups.elements("*").length();
	while ( NumGroups>0 ) 
	{
		NumGroups--;
		var Members = Registry.System.Logic.Groups.children()[NumGroups].Members.toString().split(';');
		var Orientation = Registry.System.Logic.Groups.children()[NumGroups].Orientation;
		var Anker = Registry.System.Logic.Groups.children()[NumGroups].Anker;
		var AnkerPos = parseInt(Registry.System.Logic.Groups.children()[NumGroups].AnkerPos);
		var IconLineSize = 0;
		var IconAvail = new Array();
		for ( var h = 0; h<Members.length; h++ ) 
		{
			if ( Selection.indexOf(Members[h])>=0)
			{
				var f = this.getField(Members[h]);
				if ( f!= null ) 
				{ 
					if (f.display == display.visible )
					{
						IconAvail.push(Members[h]);
						if (Orientation=="h") IconLineSize+=f.rect[2]-f.rect[0];
						if (Orientation=="v") IconLineSize+=f.rect[1]-f.rect[3];
					}
				};
			}
		}
		if ( Anker == "c" ) var IconStart = AnkerPos - IconLineSize/2;
		if ( Anker == "l" ) var IconStart = AnkerPos;
		if ( Anker == "r" ) var IconStart = AnkerPos - IconLineSize;
		if ( Anker == "b" ) var IconStart = AnkerPos;
		if ( Anker == "t" ) var IconStart = AnkerPos - IconLineSize;
		for ( var h = 0; h<IconAvail.length; h++)
		{
			var f = this.getField(IconAvail[h]);
			if (Orientation=="h")
			{
				var IconLength = (f.rect[2]-f.rect[0]);
				var r = [IconStart, f.rect[1], IconStart+IconLength, f.rect[3]];
			}
			if (Orientation=="v")
			{
				var IconLength = (f.rect[1]-f.rect[3]);
				var r = [f.rect[0], IconStart, f.rect[2], IconStart+IconLength];
			}
			f.rect = r;
			IconStart+=IconLength;
		}
	}
}


