function enableIcon ( IconName, status )
{
	var e = String(Registry.System.Logic.Enabled.toString());
	var eA = e.split(";");
	if ( status == false)
	{
		e = ";";
		for each ( var i in eA ) if (i!=IconName) e+=i+';'
	}
	if ( eA.indexOf(IconName)<0) e+=IconName+';';
	Registry.System.Logic.Enabled = e;
}

// ************ check selected icon's logic behaviour ***************************************************
function runActionLogic( IconName )
{
	if ( String(Registry.System.Logic.Active) == "true")
	{
		var s = Registry.System.Logic.Action;
		var aLines = s.split("\n");
		var cLineNum = -1;
		var x = aLines.length-1;
		while ( x>=0 )
		{
			s = aLines[x].split(";");
			if ( s[0]==IconName) { cLineNum=x; x=-1; };
			x--;
		}
		if (cLineNum>=0)
		{
			var actionIndex = -1;
			for ( var x in s ) if (s[x].indexOf("s")==0) actionIndex=x;
			if (actionIndex>=2)  // [0]=IconName; [1]=ResetStage; [2..n]=ActionCodes
			{
				Registry.System.Logic.Selection = ";"+Registry.System.Logic.Selection.toString()+IconName+';'; 									// Add selected icon-name to "Selection"-list
				//Registry.System.Logic.NameList += ";"+Registry.System.Atoms.Item.(@name==IconName).Name.toString()+';';						// Add selected item-name to "NameList"
				var e = Registry.System.Logic.Enabled.toString();																								// Delete selected Icon from "enabled-list"
				var p = e.indexOf(IconName+';');
				if (p>=0) Registry.System.Logic.Enabled = e.substr(0,p) + e.substr(p+IconName.length+1,10000);									// Save new "enabled-list"
				
				for ( var ax=0; ax<aLines.length-1; ax++)
				{
					var s = aLines[ax].split(';');
					if (s.length>0) 
					{	
						var action = s[actionIndex];
						switch ( action )
						{
							case "he":	// hide at enabled status
							{
								this.getField(s[0]).display = display.hidden;
								//Registry.System.Logic.Enabled = Registry.System.Logic.Enabled.toString() + s[0]+ ";";								// Add Icon with "enable"-action to "enabled-list"
								enableIcon(s[0],true);
							} break;
							
							case "H":
							case "hp":	// hide permanently
							{
								this.getField(s[0]).rect=[0,0,0,0];
							}	
							case "h":	// hide and disable
							{
								this.getField(s[0]).display = display.hidden;
								var e = Registry.System.Logic.Enabled.toString();																				// Delete Icon with "hide"-action from "enabled-list"
								var p = e.indexOf(s[0]+';');
								if (p>=0) Registry.System.Logic.Enabled = e.substr(0,p) + e.substr(p+s[0].length+1, 10000);						// Save new "enabled-list"
							}  break;

							case "ah":
							{
								if (IconToAdd!="") IconToAdd+=";"; IconToAdd+=s[0];																	// Add Icon to selected list from hidden and enabled status
								this.getField(s[0]).display = display.hidden;
								var e = Registry.System.Logic.Enabled.toString();																				// Delete Icon with "hide"-action from "enabled-list"
								var p = e.indexOf(s[0]+';');
								if (p>=0) Registry.System.Logic.Enabled = e.substr(0,p) + e.substr(p+s[0].length+1, 10000);						// Save new "enabled-list"
							}  break;
							
							case "E":
							case "eh":	// enable icon from hidden state
							{
								this.getField(s[0]).display = display.visible;
							}
							case "e":	// enable icon from disabled state
							{
								Registry.System.Logic.Enabled = Registry.System.Logic.Enabled.toString() + s[0]+ ";";								// Add Icon with "enable"-action to "enabled-list"
								iconMouseOutHandler( s[0] );																						// from "d"-disabled status
							}  break;
							
							case "D":
							case "dh":
							case "d":	// disable icon from any state
							{
								Registry.System.Logic.Enabled = Registry.System.Logic.Enabled.split(";"+s[0]+";").join(";");						// Remove the icons from enabled list
								this.getField(s[0]).display = display.visible;																		// make icon visible
								iconMouseOutHandler( s[0] );																						// from "h"-hidden status
							}  break;

							case "v":	// make icon visible
							{
								this.getField(s[0]).display = display.visible;																		// make icon visible
							}  break;

							case "a":	// add from any state
							{
								if (IconToAdd!="") IconToAdd+=";"; IconToAdd+=s[0];																	// Add Icon to selected list from hidden and enabled status
								var fx = this.getField(s[0]);
								if ( fx != null )
								{
									fx.display = display.visible;
									Registry.System.Logic.Enabled += s[0]+';';
								}
							}  break;

							case "ae":	// add from enabled state only
							{
								var en = Registry.System.Logic.Enabled.toString().indexOf(";"+s[0]+";");
								if ( en<0 ) break;
								if (IconToAdd!="") IconToAdd+=";"; IconToAdd+=s[0];
							}  break;
							
							default:
							{
							   if ( action.charAt(0) == "s" )
								{ console.clear();
									var SubTable = action.split(",");	// split action-string to ["s",subtable1,...subtableN]
									for ( var subTabNum in SubTable ) if (subTabNum>0)	// run over all subtables if available ( except table[0] as it is "s" )
									{
										var tab = Registry.System.Logic.SubTable.elements(SubTable[subTabNum]).toString().split("\n");	// create table-lines in tab-array
										var headLine = tab[0].split(";");	// separate headline as name-string-array
										var colNum = headLine.length;	// detect number of columns
										//var logic = new Array();	// create logic-array
										var logic = [];	// create logic-array
										for ( var x=0; x<colNum; x++) // preset the logic-array with already selected and current icon-status
										{
											if ( headLine[x]==IconName ) logic.push(1); else logic.push(0);
											if ( isSelected ( headLine[x] ) == true ) logic[x] = 1;
										}
										for ( var xLine in tab ) if ( xLine>0 ) // delete all tab-lines that do not match the logic's "1"
										{
											var line = tab[xLine].split(";");
											for ( var x=0; x<colNum; x++ ) if ( (logic[x]==1) && (line[x]==0) ) tab[xLine]="";
										}

													
										Registry.System.Logic.Valid = Registry.System.Logic.Valid.toString().replace(SubTable[subTabNum].charAt(0),"");	// remove SubTable-Character from Valid-String by default

										var lx = logic.join(";");
										for ( var xLine in tab ) if ( xLine>0 )	// run over 
										{
											if ( lx == tab[xLine] ) Registry.System.Logic.Valid += SubTable[subTabNum].charAt(0);	// add SubTable-Character to Valid-String, if subtable logic okay
										}

										for ( var xLine in tab ) if ( xLine>0 ) // set all "1" from the remaining tab-lines in logic
										{
											var line = tab[xLine].split(";");
											for ( var x=0; x<colNum; x++ ) if ( (logic[x]==0) && (line[x]==1) ) logic[x]=1;
										}

										for ( var x in logic ) // hide or enable all icons depending on the logic
										{
											if ( logic[x]==0 ) this.getField(headLine[x]).display = display.hidden;

											if ( (logic[x]==1) && ( isSelected(headLine[x]) == false ) )	// automatic enabling of logic's == 1
											{
												//Registry.System.Logic.Enabled = Registry.System.Logic.Enabled.toString() + headLine[x]+ ";";									// Add Icon with "enable"-action to "enabled-list"
												//iconMouseOutHandler( headLine[x] );
											}
											
											console.println(headLine[x]+'='+logic[x]);
											
											var e = Registry.System.Logic.Enabled.toString();																								// Delete selected Icon from "enabled-list"
											var p = e.indexOf(IconName+';');
											if (p>=0) Registry.System.Logic.Enabled = e.substr(0,p) + e.substr(p+IconName.length+1,10000);									// Save new "enabled-list"

										}
									}
								}
							}  break;
						}
					}
				}

				var s = aLines[aLines.length-1].split(';');
				if ( s[0] == "SysValid" )	// if SysValid-line is reached
				{
					Registry.System.Logic.Valid += s[actionIndex];	// add the SysValid-character of the action-index if available
					var vc = Registry.System.Logic.Valid.toString();	// get the Valid-string
					var vd = Registry.System.Logic.Validation.toString().split(";");	// get the Validation-strings as array
					var f = this.getField("Btn_IncompleteSys");	// get the incomplete-message-field
					f.delay = true;	// do not refresh the field
					f.display = display.visible;	// hide field by standard
					for ( var v=0; v<vd.length; v++ )	// run over all Validation-strings
					{
						var valid = true;
						for ( var h=0; h<vd[v].length; h++)	// run over the current Validation-string's characters
							if (vc.indexOf(vd[v].charAt(h))<0) valid = false;	// if character not in Valid-string, set valid to false
						if ( valid == true ) f.display = display.hidden;
					}
					f.delay = false;	// refresh field
				}
			}
		}
	}
}


// ************ reset all icon-logic to default values ***************************************************
function resetActionLogic()
{
	this.getField("Btn_IncompleteSys").display = display.hidden;
	IconChain=[];
	IconLinkTo=[];
	if ( String(Registry.System.Logic.Active) == "true")
	{
		IconToAdd="";
		Registry.System.Logic.Clicked = "";	// clean-up icon selection history
		Registry.System.Logic.Selection = "";	// clean-up Icon selection list
		Registry.System.Logic.Enabled = "";	// 
		Registry.System.Logic.IconStatus = ";";
		Registry.Internals.settings.finalised.@value = true;	// mark the current Registry-status as finalised
		var s = Registry.System.Logic.Action;
		var aLines = s.split("\n");
		for ( var x=0; x<aLines.length; x++)
		{
			var s = aLines[x].split(';'); // s[0]=IconName; s[1]=ResetStage; s[2..n]=ActionCodes
			if (s.length>0) 
			{
				if (s[1] == "e")   
				{	
					Registry.System.Logic.Enabled = Registry.System.Logic.Enabled.toString() + s[0]+ ";";
					iconMouseOutHandler( s[0] );
				}
				if (s[1] == "d")
				{	
					var f = this.getField(s[0]); if (f!=null)
					{
						f.lineWidth =2;
						f.strokeColor=["T"];
						f.fillColor = ["T"];
					}
				}
				if (s[1] == "h")   
				{	
					var f = this.getField(s[0]);
					if (f!=null) f.display = display.hidden;
				}

				if (s[1] == "he")   
				{	
					var f = this.getField(s[0]);
					if (f!=null) f.display = display.hidden;
					enableIcon(s[0],true);
				}
			
			}
			if ( s[0] == "SysValid" ) Registry.System.Logic.Valid = s[1];
		}
		registrySave();
	}
	console.clear();
}


// ************ finalise the action logic with selection and deselection ***************************************************
function finaliseActionLogic()
{
	if ( String(Registry.System.Logic.Active) == "true")	// stop at deactivated logic
	if ( String(Registry.Internals.settings.finalised.@value.toString()) == "false")	// run only, if not alredy finalised
	{
		var sel = ";"+String(Registry.System.Logic.Selection.toString());
		Registry.System.Logic.Enabled = "";
		var s = Registry.System.Logic.Action;
		var aLines = s.split("\n");
		for ( var x=0; x<aLines.length; x++)
		{
			var s = aLines[x].split(';'); // s[0]=IconName; s[1]=ResetStage; s[2..n]=ActionCodes
			if (s.length>0) 
			{
				if ( sel.indexOf(s[0]+';') <0 )
				{
					var f = this.getField(s[0]);
					if (f != null) f.display = display.hidden;
				}
			}
		}
		var sel = ";"+String(Registry.System.Logic.Selection.toString());
		var iStat = ";";

		for each ( var i in Registry.System.Logic.FunctionGroups.Activation.children() ) if ( sel.indexOf(";"+i.name()+";") >=0 ) iStat += i.toString()+';';	// add the item to activate from Sprite-Activation-List
		for each ( var i in Registry.System.Logic.FunctionGroups.Choice.children() ) if ( sel.indexOf(";"+i.name()+";") >=0 )	// add the single or multiple choice items to the selection
		{
			var selItems = i.@items.split(";");	// split all selected items by ";"
			for each ( var it in selItems )	
			{
				iStat += it.split(":")[0]+';';	// split all separated items by ":" to isolate the selected tag from choice in selItems[0]
				//app.alert("it:\n"+it+"\n selItems:\n"+selItems+"\n iStat:\n"+iStat);
			}
		}
		for ( var i = 0; i<Registry.System.Logic.FunctionGroups.Deactivation.elements().length(); i++)
		{
			var deactivatorName = Registry.System.Logic.FunctionGroups.Deactivation.elements()[i].name();
			if ( sel.indexOf(";"+deactivatorName+";") >=0 )
			{
				var deactivationList = String(Registry.System.Logic.FunctionGroups.Deactivation.elements()[i].toString()).split(";"); //con("deact:"+deactivationList);
				for ( var j = 0; j<deactivationList.length; j++ ) if ( iStat.indexOf(";"+deactivationList[j]+";") >=0 ) { iStat = iStat.replace( ";"+deactivationList[j]+";",";");} //con(iStat); }
			}
		}
		Registry.System.Logic.IconStatus = iStat;
		
		// finalize section
		var itemsToAdd=[];
		var itemsToRem=[];
		for each ( var i in Registry.System.Logic.FunctionGroups.Finalize.children() ) 
		{
			var result = false;
			var fkt = String(i.@fkt.toString()).toUpperCase();
			var itemA = String(i.@item.toString()).split(";");
			var condA = String(i.@cond.toString()).split(";");
			var value = String(i.toString());
			
			//if (fkt == "EXEC" ) eval(value); else
			if (fkt == "ACTIFALL" )	// activate if all condition-items match
			{
				var lgx = true;
				for each( var cond in condA) 
				{
					if ( isSelected(cond) == false )
					if ( itemsToAdd.indexOf(cond)<0 ) lgx = false;
				}
				if (lgx == true)	// if reference item's logic matches (all conditions met)
					for each (var item in itemA)	// run over all items to activate
						if (isSelected(item) == false)		// find items at false status to be handled only
						{
							var itemName = item;
							if ( itemName.indexOf("!")>=0 ) itemName=item.slice(item.indexOf("!")+1);	// create item-name without "!"
							if ( itemName == item ) itemsToAdd.push(itemName);	// non-negative acting (TAG)
							else itemsToRem.push(itemName);		// negative acting (!TAG)
						}
			} else

			if (fkt == "ACTIFONE" )	// activate if minimum one condition-item matches
			{
				var lgx = false;	// logix default false to raise any true item
				for each( var cond in condA)	// run over all condition-items in condA
				{
					if ( isSelected(cond) == true ) lgx = true;	// if icon or tag selected, logix=true
					if ( itemsToAdd.indexOf(cond)>=0 ) lgx = true;	// if icon or tag to be added, logix=true
				}
				if (lgx == true)	// if reference item's logic matches (at least one condition met)
					for each (var item in itemA)	// run over all items to activate
						if (isSelected(item) == false)		// find items at false status to be handled only
						{
							var itemName = item;
							if ( itemName.indexOf("!")>=0 ) itemName=item.slice(item.indexOf("!")+1);	// create item-name without "!"
							if ( itemName == item ) itemsToAdd.push(itemName);	// non-negative acting (TAG)
							else itemsToRem.push(itemName);		// negative acting (!TAG)
						}
			} //else
			
			if (String(value.toString()).length>0) eval(value);
		}
		// app.alert("itemsToAdd:"+itemsToAdd+"\nitemsToRem:"+itemsToRem);
	}
	for each ( var item in itemsToAdd )	// run over all items in list to add
	{
		if ( this.getField(item) == null)	// is this item a TAG ?
		{
			Registry.System.Logic.IconStatus = String(Registry.System.Logic.IconStatus.toString())+';'+item+';';	// add the TAG to IconStatus
		}
		else	// this item is an ICON
		{
			Registry.System.Logic.Selection = String(Registry.System.Logic.Selection.toString())+';'+item+';';	// add the icon-name to Selection
			setIconToStage ( item, "" );	// set icon to next logic stage
		}
	}
	
	for each ( var item in itemsToRem )	// run over all items in list to remove
	{
		if ( this.getField(item) == null)	// is this item a TAG ?
		{
			var i = String(Registry.System.Logic.IconStatus.toString());
			var iA = i.split(item);
			Registry.System.Logic.IconStatus = iA.join(";");	// join the remaining TAGs to IconStatus
		}
		else	// this item is an ICON
		{
			var i = String(Registry.System.Logic.Selection.toString());
			var iA = i.split(item);
			Registry.System.Logic.Selection = iA.join(';');	// join the remaining icon-names back to Selection
			setIconToStage ( item, "!" );	// set icon to start logic-stage and hide
		}
	}	
	Registry.Internals.settings.finalised.@value = true;	// mark the current Registry-status as finalised
	registrySave();
}




function resetIconStages ()
{
	IconLinkTo = [];
	var iNum = Registry.System.Atoms.Item.length();
	for ( var iRun = 0; iRun<iNum; iRun++)
	{
		var zeroStageName = Registry.System.Atoms.Item[iRun].ZeroStage.toString();
		var iName = Registry.System.Atoms.Item[iRun].@name.toString();
		setIconToStage( iName, zeroStageName );
	}
	registrySave();
}


function arrangeIcons ()
{
	IconChain.sort();
	var sysSize = 0.0;
	for ( var x in IconChain )
	{
		var a = IconChain[x].split(',');
		sysSize += parseFloat(a[3])-parseFloat(a[1]);
	}
	var pRect = this.getPageBox();
	var lStart = (pRect[2]-sysSize) / 2;
	for ( var x in IconChain ) 	
	{
		var a = IconChain[x].split(',');
		var x1 = lStart + parseFloat(a[5]) - parseFloat(a[1]);
		var x2 = x1 + parseFloat(a[7]) - parseFloat(a[5]);
		this.getField( a[9] ).rect = [x1, parseFloat(a[6]), x2, parseFloat(a[8]) ];
		lStart += parseFloat(a[3]) - parseFloat(a[1]);
	}
}




// ************ set Icon to given stage ***************************************************
function setIconToStage ( IconName, StageName )
{
	var iNum = Registry.System.Atoms.Item.(@name==IconName).childIndex();

	if ( StageName == "" )	// set to next logic stage
	{
		var currentStageName = String(Registry.System.Atoms.Item[iNum].Stage.toString())+'';
		var currentStageData = XML( Registry.System.Atoms.Item[iNum].Stages.elements(currentStageName) );
		StageName = String(currentStageData.nextStage.toString());
	}
	
	var remove = false;
	if ( StageName == "!" )	// set to start logic-stage and hide
	{
		var currentStageName = String(Registry.System.Atoms.Item[iNum].Stage.toString())+'';
		var currentStageData = XML( Registry.System.Atoms.Item[iNum].Stages.elements(currentStageName) );
		var dockInfo = String(currentStageData.dock.toString());
		if ( dockInfo!="")
		{
			var dockPos = dockInfo.split(",")[0];
			var icTemp = [];
			for each ( var ic in IconChain ) if ( ic.split(',')[0] != dockPos ) icTemp.push(ic);
			IconChain = [];
			for each ( var ic in icTemp ) IconChain.push(ic);
		}
		StageName = String(Registry.System.Atoms.Item[iNum].ZeroStage.toString());
		remove = true;
		arrangeIcons();
		checkIconGrouping();	// ########################################################################
	}
	
	Registry.System.Atoms.Item[iNum].Stage = StageName+'';
	var stageData = XML( Registry.System.Atoms.Item[iNum].Stages.elements(StageName) );
	var f = this.getField(IconName);
	if (f == null) return;
	f.delay = true;
	f.borderStyle = stageData.borderStyle;
	f.alignX = stageData.alignX;
	f.alignY = stageData.alignY;
	f.buttonPosition = stageData.position;
	f.buttonScaleHow = stageData.scaleHow;
	f.buttonScaleWhen = stageData.scaleWhen;
	f.display = stageData.display; 
	f.fillColor = colorStrToColor( stageData.fillColor ); 
	f.highlight = stageData.highlight;
	f.lineWidth = stageData.lineWidth;
	f.page = stageData.page;
	if ( String(stageData.linkTo.toString()) != "" )	// if linkTo-data available (this icon is linked to a parent-icon)
	{
		var lx = String(stageData.linkTo.toString()).split(",");	// get the linkTo-data
		IconLinkTo.push( IconName +','+ String(stageData.rect.toString()) + "," + lx  );	// add the icon to the linkedTo-list
	}
	var chainCheck = ","; 	// create a docking-ID-string-list
	for ( var x in IconChain ) chainCheck += String(IconChain[x]).split(",")[0]+',';	// extract the icon's docking ID's
	var dockStr = String(stageData.dock.toString());	// get the docking position string
	if ( dockStr != "" ) 	// if docking-position defined ...
	{
		if ( chainCheck.indexOf( ','+dockStr.split(',')[0]+',') < 0)	// check, if icon not already in list
		{
			IconChain.push( dockStr+','+String(stageData.rect.toString())+','+IconName );	// add the icon to the list
			arrangeIcons();	// re-arrange the listed icons
		}
	}  else f.rect = stageData.rect.split(",");	// ... else just set the stage position and dimensions (rect)
	
	for ( var x in IconLinkTo )	// run over all LinkTo-icon-data and arrange the icons
	{
		var lx = String(IconLinkTo[x]).split(",");	// get linkTo-entry
		var r = this.getField( lx[5] ).rect;	// get the current rect-positions of the partent-icon to link to
		var fd = this.getField( lx[0] );	// get the current icon to be linked to
		var w = lx[3]-lx[1];	// calculate the width
		var h = lx[2]-lx[4];	// calculate the height
		var dx = parseFloat(lx[6]);	// calculate the related delta-x position
		var dy = parseFloat(lx[7]);	// calculate the related delta-y position
		fd.rect = [  r[0]+dx, r[3]+h+dy, r[0]+w+dx, r[3]+dy ];	// set the icon to the linked position
	}

	f.readonly = [false,true][stageData.readonly];	// set icon's read-only status
	f.rotation = [0,90,180,270][stageData.rotation / 90];	// set icon-rotation
	f.strokeColor = colorStrToColor( stageData.strokeColor );	// set frame-colour
	f.textColor = colorStrToColor( stageData.txtCol );	// set font/text-colour
	f.textFont = stageData.txtFont;	// set icons font type
	f.textSize = stageData.txtSize;	// set icons text size
	f.buttonSetCaption( stageData.caption );	// set button text (caption)
	f.delay = false;	// stop delayed drawing
	if ( remove==true) this.getField(IconName).display = display.hidden;	// if icon has to be removed, it has to be hidden as well
}

//***********************************************************************************************************
// checks, if a given item-name (ICON, TAG, !ICON, !TAG) is selected in the current system-selection
//***********************************************************************************************************
function isSelected ( name )
{
	var negativeLogic = false;	// items with "!"-prefix are handled as negative logic (is-not-selected)
	if (name.indexOf("!")>=0)	// detect negative logic items
	{
		negativeLogic=true; 	// mark negative logic is true
		name=name.slice(name.indexOf("!")+1);	// remove the "!" from item-name
	}
	var s = ';'+String(Registry.System.Logic.Selection.toString());	// default check for item-name in Selection (ICON-list)
	var p = s.indexOf(";"+name+";");	// get string-position of the item
	if ( p<0 )	// if sub-string not found (<0)
	{
		var s = ';'+String(Registry.System.Logic.IconStatus.toString());	// check for name in IconStatus (TAG-list)
		var p = s.indexOf(";"+name+";");	// get string-position of item
		if ( p<0 ) return negativeLogic;	// return false for "not selected"
		return !negativeLogic;	// return result for "TAG selected"
	}
	else return !negativeLogic;	// return result for "ICON selected"
}

