//*******************************************************************************************************
//*** Initialise the Library-variable and prepare Button-captions and -hints
//*******************************************************************************************************
function Library_Init()
//*******************************************************************************************************
{
	Library = new XML( this.getField("Babelfisch").value);	// Load Library string-tables from "Babelfisch"
	lcidRef = Library.ReferenceLanguage.@lcid;	// load reference language id (LCID)
	lcidInUse = Library.CurrentLanguage.@lcid;	// load currently used language id (LCID)
	currentLanguageName = Library.CurrentLanguage.@name.toString();
	currentLanguageText = Library.CurrentLanguage.@text.toString();
	var childIdx = Library.Language.(@lcid == lcidInUse).childIndex();	// get current language child-index
	PageList = {}; PageTextList = [];	// init PageList and PageTextList
	var xList = Library.children()[childIdx].elements().(@type=="pn");	// load "pn"-page names in xList
	for each ( var pn in xList ) { PageList[pn.@text.toString()] = pn.@name.toString(); PageTextList.push(pn.@text) };	// set strings in pagename-lists
	setHintsAndCaptions();	// init hint and caption texts
}

//*******************************************************************************************************
//*** Save the Library-variable-content to the "Babelfish"-field
//*******************************************************************************************************
function Library_Save()
//*******************************************************************************************************
{
	this.getField("Babelfisch").value = Library;
}

//**********************************************************************************************************************
// set language depending hint text of buttons and sprites (  mouse-over hint )
//**********************************************************************************************************************
function setHintsAndCaptions()
{
	var childIdx = Library.Language.(@lcid == Library.CurrentLanguage.@lcid).childIndex();	// get the current language's child-index
	var xList = Library.children()[childIdx].elements().(@type=="sh");	// get the sprite-hint list
	for each ( var sh in xList ) {	var f = this.getField( sh.@name ); if ( f != null ) f.userName = sh.@text }	// set the sprite-hints
	var xList = Library.children()[childIdx].elements().(@type=="un");	// get the user-name list
	for each ( var un in xList ) {	var f = this.getField( un.@name ); if ( f != null ) f.userName = un.@text }	// set the user-names
	var xList = Library.children()[childIdx].elements().(@type=="bc");	// get the button-caption list
	for each ( var bc in xList ) {	var f = this.getField( bc.@name ); if ( f != null ) f.buttonSetCaption( bc.@text ) }	// set the button caption texts
}

//*******************************************************************************************************
//*** Show Hyperpage by language-depending name in pageText or @+pageName
//*******************************************************************************************************
function showPage ( pageText, activateMacros )
//*******************************************************************************************************
{
	if ( pageText == "" ) return;
	PageText = pageText;
	PageName = PageList[PageText];
	if ( ( PageName==undefined ) && ( pageText.indexOf("@")==0 )) PageName = pageText.split("@")[1];
	PageLink = PageName;
	var PageCode = "";  // Load xml-page-description-code to PageCode 
	PageCode = Library.HyperPage.elements([PageName]).toString();
	var f = this.getField("InfoBoard");	f.value = "This page is not created yet";
	if ( PageCode != "" ) f.richValue = xmlToRich( PageCode, activateMacros, "", "" );
}


//*******************************************************************************************************
//*** Save Hyperpage-raw-text to the current selected Hyperpage-data
//*******************************************************************************************************
function savePage ()
//*******************************************************************************************************
{
	var f = this.getField("InfoBoard");
	var PageCode = richToXML ( f.richValue, f.value );
	if ( PageLink != "" )
	{
		var hyperIdx = Library.HyperPage.elements([PageLink]).childIndex();
		Library.HyperPage.children()[hyperIdx] = PageCode;
	}
	Library_Save();
}



//*******************************************************************************************************
//**** Convert RichText-spans to XML-data from richValue and textValue of a edit-field
//*******************************************************************************************************
function richToXML ( richTextValue, textValue )
//*******************************************************************************************************
{
	var spans = richTextValue;
	for ( var i=0; i<spans.length; i++) 
	{
		if ( spans[i].text+'' != "" ) textValue = textValue.substr( spans[i].text.length, textValue.length);  // detect control chars [EOL,LF...] in original-text-line
		while ( textValue.charCodeAt(0) < 32 ) // if control-chars detected,
		{
			spans[i].text = spans[i].text+textValue.charAt(0); // add the control-char to the spantext 
			textValue = textValue.substr(1, textValue.length); // remove the added char from original text
		}
	}
	return util.spansToXML(spans); // return compressed spans with control-char as xml-text
}


//*******************************************************************************************************
//**** Restore Richtext-Spans from XML-converted Richtext and replace macro's by JavaScript eval-calls
//*******************************************************************************************************
function xmlToRich ( xmlText, macrosActive, macroStartMarker, macroEndMarker )
//*******************************************************************************************************
{
	txtVisible = true;	// preset the text-visibility-variable to true ( for TXON/TXOFF )
	if ( macroStartMarker == "" ) macroStartMarker = "[["; // set default macroStartMarker
	if ( macroEndMarker == "" ) macroEndMarker = "]]"; // set default macroEndMarker
	var macroA = xmlText.split( macroEndMarker );	// split the XML-span-string at macro-end-markers
	for ( var mc=0; mc<macroA.length; mc++ )	// run over the macro-string-array
	{
		var macroB = macroA[mc].split( macroStartMarker );	// split the macro's at macro-start-markers
		if ( macroB.length>1 )	// if macro-string-list contains more then one string
		{
			while ( macroB[1].length > macroB[1].replace("&quot;",'"').length ) macroB[1] = macroB[1].replace("&quot;",'"');	// convert all "
			while ( macroB[1].length > macroB[1].replace("&amp;",'&').length ) macroB[1] = macroB[1].replace("&amp;",'&');	// convert all &
			try { var errorMacro = macroB[1]; macroB[1] = eval(macroB[1]) }	// set the macro-value by the macro-eval-result
			catch(e) { app.alert("Macro error: "+ errorMacro ) };	// show errormessage dialog
		}
		macroA[mc] = macroB.join( "" );	// join the macro-parts
	}
	var txtOn = macroA.join("").split(txtOnCode);	// split the whole text by text-on-codes
	for ( var txtoo = 0; txtoo<txtOn.length; txtoo++ ) { txtOn[txtoo]=txtOn[txtoo].split(txtOffCode)[0] };	// run over the text-on-strings and split the text-on-string by text-off-codes and keep the text-on-part only
	var raw = txtOn.join("");
	var s = raw.replace("\\t","\t"); while ( raw.length != s.length ) { raw=s; s=raw.replace("\\t","\t") };	// convert all tabs to \t
	var s = raw.replace("\\T","\t"); while ( raw.length != s.length ) { raw=s; s=raw.replace("\\T","\t") };	// convert all Tabs to \t
	var s = raw.replace("\\r","\r"); while ( raw.length != s.length ) { raw=s; s=raw.replace("\\r","\r") };	// convert all returns to \r
	return util.xmlToSpans ( raw ); // return the rich text span'S
}


//*******************************************************************************************************
//**** Round any floatingpoint value to a defined number of digits
//*******************************************************************************************************
function Round( value, digits )
//*******************************************************************************************************
{
	var x = value;
	if ( isNaN(x)== true ) x = 0; // NaN handling ************************
	if ( digits <= 0 ) return Math.round( value );
	for ( var d=0; d<digits; d++ ) x *= 10;
	x = Math.round(x);
	if ( digits>0 ) for ( var d=0; d<digits; d++ ) x /= 10;
	x = String(x);
	if ( x.indexOf(".") < 0 ) x += '.';
	x += '000000000000000000000000000000000000000000000';
	var p = x.indexOf(".") + digits + 1;
	x = x.substr( 0, p );
	return x;
}


//*******************************************************************************************************
//**** Get the STRing value from choosen language string-table and init the value if "text" is given
//*******************************************************************************************************
function STR( strName, text, strType )
//*******************************************************************************************************
{
	var result = "??STR(\""+strName+"\")??";
	var stringType = "gs"; if ( arguments.length >= 3 ) stringType = strType;
	if ( ( String(Library.MacroWriting.toString()) != "true" ) || ( arguments.length == 1 ) )
	{
		var langIdx = Library.Language.(@lcid == Library.CurrentLanguage.@lcid).childIndex();
		result = Library.children()[langIdx].elements().( ( @type==stringType ) && ( @name==strName ) ).@text.toString().split("\\T").join("\t");
		if ( result == "" ) result = "??(\""+strName+"\")??";
	} else result = STRA( strName, text, stringType );
	return result;
}

function STRA( strName, text, strType ) // update or create new string-entry in library table
{
	text = text.split("&#x9;").join("\\T");	
	text = text.split("\t").join("\\T");	
	var stringType = "gs";	if ( arguments.length >=3 ) stringType = strType;
	var langIdx = Library.Language.(@lcid == lcidRef).childIndex(); // get language-index for reference language lcid ( english==9 )
	var xList = Library.children()[langIdx].elements().( (@type==stringType) && (@name==strName) );	// try to get the requested string
	if ( xList.length() == 0 )	// if string does not exist, create it new
	{
		var sx = <String/>;	sx.@type = stringType;	// setup new child-string
		sx.@name = strName;	sx.@text = text;
		Library.children()[langIdx].insertChildAfter( null, sx );	// insert new child on position 0 in the list
	}
	else	// if string exists, 
	{
		var stringIdx = Library.children()[langIdx].elements().( ( @type == stringType ) && ( @name==strName ) ).childIndex(); // get the list-index of the string
		Library.children()[langIdx].elements()[stringIdx].@text = text;	// set the text-parameter
	}
	Library_Save();	// save the Library after new creatin or change of a string
	return text.split("\\T").join("\t");
}


