var guid = "";	// web-browser URL parameter ( UID )
var srvAddress = "";	// server-address to submit data
var srvFileName = "ProcessArticleList.ashx#FDF";	// default filename on server
var srvProtocol = "HTML";	// Transmision protocol
var srvMessage = "";	// message-string
var submitParam = {};

var inTransmission = false;

//**************************************************************************************************************************
//*** Setup the transmission-environment ( gui, server-address and submit-button )
//**************************************************************************************************************************
function transmitInit()
{
	submitParam.cSubmitAs = srvProtocol;	// define submit-protocol type
	submitParam.aFields = ["Transmitter"];	// data-saving field name
	submitParam.cCharset = "utf-8";	// character set used in transmitted data
	if ( Registry.Internals.settings.detectExternal.@value==true )	// if browser detection is enabled
	if ( this.external == true )	// if opened in browser via web-url
	if ( this.URL.indexOf("?") >= 0 )	// if get-parameters added with "?" as marker
	if ( this.URL.length > this.URL.indexOf("?") )	// if marker "?" is followed by additional parameters
	{
		var guidA = this.URL.substring(this.URL.indexOf("?") + 1).toLowerCase().match(/%7b(.+)%7d/);	// extract guid parameter-list-array
		if ( guidA.length > 0 ) guid = guidA[1].toString();	// extract guid-parameter #1
		srvAddress = this.URL.substring(0, this.URL.lastIndexOf("/") + 1);	// extract the url-address
		submitParam.cURL = srvAddress + srvFileName;	// create cURL-address in submit-params
		if ( ( guid != "" ) && ( srvAddress != "" ) ) 	// if guid & srvAddress exist
		{
			Registry.Internals.menuItems.Btn_SubmitCRM.@visible=true;	// ... if browser, guid and srvAddress found, SubmitCRM-btn will be enabled
			arrangeMenuButtons();	// rearrange the menu-buttons
		}
	}
}

//**************************************************************************************************************************
//*** Do the transmission to the CRM / called by ButtonHandler-Button
//**************************************************************************************************************************
function transmitToCRMSystem()
{
	inTransmission = true;
	var oXML = new XML( createXMLOrder().toString() );	// create order-XML-value
	oXML.configurationtarget.idsource = "FalconCRM";	// set XML-target-ID-source
	oXML.configurationtarget.idtypename = "opportunityproduct";	// set XML-target-ID-type
	oXML.configurationtarget.id = guid;	// set XML-order id-code to opportunity-id
	oXML.foliant.url = srvAddress;	// store XML-foliant-data target-server-address
	this.getField("Transmitter").value = '<?xml version="1.0" encoding="utf-8"?>\n' + oXML.toString();	// add XML-version- and char-code-tag
	var txt = STR("dlgSubmitToCRM","Do you want to submit the current configuration?","dc");
	txt+= "\rGUID:"+guid;
	txt+= "\rURL:"+srvAddress;
	txt+= "\rFILE:"+srvFileName;
	var nButton = app.alert({cMsg: txt, cTitle: STR("dlgSubmitToCRMCapt","Submit configuration","dc"), nIcon: 2, nType: 2});
	if ( nButton == 4 ) 
	{
		//this.submitForm({ cURL: srvAddress + srvFileName, aFields: ["Transmitter"], cSubmitAs: srvProtocol });	// submit the form-data
		this.submitForm( submitParam );	// submit the form-data from submitParam-configuration
	}
	this.getField("Transmitter").value = "";
	inTransmission = false;
}

//**************************************************************************************************************************
//*** Initialise Foliant from XFDF-data loaded to field "Transmitter"
//**************************************************************************************************************************
function transmitInitFromXFDF() 
{
	var s = this.getField("Transmitter").value.split("?>");
	for ( var x in s ) if ( s[x].indexOf("<?") >= 0 ) s[x] = s[x].substr( 0, s[x].indexOf("<?") ); s = s.join("\n");
	if ( s.indexOf("<order") >= 0 )
	{
		var cfg = new XML( s ); 
		if ( String(cfg.foliant.toString()) == "" ) return;
		if ( String(cfg.foliant.idCode.toString()) != "" ) if ( cfg.foliant.idCode.toString() != Registry.Internals.ident.idCode.@value.toString() ) 
		{
			var msg = STR("dlgWrongFDFData","Configuring from loaded FDF-Data not possible! Foliant-idCode and FDF-idCode mismatch!","dc");
			msg += '\r\rFoliant-ID: '+Registry.Internals.ident.idCode.@value.toString()+"\rXFDF-ID: "+cfg.foliant.idCode.toString();
			app.alert({cMsg: msg, cTitle:"XFDF-Error"});
			return;
		}
		var langXFDF = String(cfg.foliant.language.toString());		// get the CRM-Language from FDF
		if ( langXFDF != "" )
		{
			var langX = Library.Language.(@lcid == langXFDF)+"";	// get the language by lcid-code
			if ( langX != "" )	// if langXFDF in langX and not empty ... else keep the current language
			{
				Library.CurrentLanguage.@lcid = langXFDF;	// set the lcid-code in Library
				Library.CurrentLanguage.@name = Library.Language.(@lcid == langXFDF).@name;	// set the language name in Library
				Library_Save();	// save the Library to Babelfish
				Library_Init();	// re-init the Library
			}
		}
		resetAll();	// reset the selection
		for each ( var ci in cfg.foliant.Choices.children() ) // init the choices of multiple-selectable icons
		{
			if ( String(Registry.System.Logic.FunctionGroups.Choice.child(ci.localName()).toString())+"" != "" )
			Registry.System.Logic.FunctionGroups.Choice.child(ci.localName()).@items = ci;
		}
		registrySave();
		var pageName = cfg.foliant.visiblePage.toString();	// available pageName as indicator for finalised selection 
		if ( pageName != "" )	// if pageName is available, continue with configuring the Foliant, else keep the Foliant in reset stage
		{
			var cfgList = cfg.foliant.Clicked.split(";");	// get the list of clicked icons ( no added !! )
			backSelection = true;	// set backSelection for automatic choice-item selection during virtual icon-selection
			for ( var x = 0; x<cfgList.length; x++ ) iconHandler(cfgList[x]);	// virtually re-click the listed icons for reconfiguration
			backSelection = false;	// reset backSelection to standard
			var iconsMissed = "";	// string to collect the missed icon names
			var oSel = cfg.foliant.Selection.toString().split(";");	// saved order-selection as string-array
			var fSel = Registry.System.Logic.Selection.toString();	// selection-entries in foliant after reconfiguration as array
			while ( fSel.indexOf(";;")>=0 ) fSel = fSel.replace(";;",";"); fSel = fSel.split(";");	// cleanup the string from empty entries ;;;;
			for ( var x in oSel ) if ( fSel.indexOf(oSel[x]) < 0 ) { iconsMissed += oSel[x]+','; iconHandler( oSel[x] ); }	// if a icon is not selected after reconfiguration, virtually re-click the icon
			finaliseActionLogic();	// finalise the selection to genreate the lists for IconStatus
			var itemsMissed = "";	// string to collect the names of missed items
			var oStat = cfg.foliant.IconStatus.toString().split(";");	// create array from the orders itemlist
			for ( var x in oStat ) if ( isSelected(oStat[x]) == false ) itemsMissed += oStat[x]+', ';	// run over the IconStatus to compare selected items in Foliant and List
			var msg = STR("dlgConfigLogicError","The following items were not selected during the automatical reconfiguration from the loaded FDF-Data due to a system-validation-logic change!","dc");
			if ( itemsMissed+iconsMissed != "" ) app.alert( msg + '\r\rItems:'+itemsMissed+'\r\rIcons:'+iconsMissed);	// information dialog about missed items
			this.getField("LogicChart").display = display.hidden;	// hide the Logic-Chart for result-page displaying
			showPage ( "@"+pageName, true ); Library.VisiblePage = pageName; Library_Save();	// generate and show the results-page
		}
		// set the parameter values
		if ( cfg.foliant.targetURL+"" != "" ) srvAddress = cfg.foliant.targetURL.toString();
		if ( cfg.foliant.targetFileName+"" != "" ) srvFileName = cfg.foliant.targetURL.toString();
		if ( cfg.foliant.targetProtocol+"" != "" ) srvProtocol = cfg.foliant.targetProtocol.toString();
		if ( cfg.foliant.targetMessage+"" != "" ) srvMessage = cfg.foliant.targetMessage.toString();
		// set the defined values from XFDF-XML-structure
		for each ( var x in cfg.foliant.submitParam.children() )	// parse and setup the submitForm-parameters from Transmitter
		{
			var value = x.@value.toString();
			var name = x.localName();
			switch ( name )
			{
				case "aFields": { value = value.split(",") }; break;
				case "aPackets": { value = value.split(",") }; break;
			}
			submitParam[name] = value;
		} 
	}
	markedDirty = false;	// set dirty to "un-changed"
	this.dirty = markedDirty;
}


//***********************************************************************************************************
// create order-list as XML-structure from system-selection
//***********************************************************************************************************
function createXMLOrder()
{
	LISTLOAD("ArticleCodes");	// load list-data from Pandora-Table area
	var nameColumn = IniListArray[0].split(";").indexOf("name");	// detect name-column number
	var codeColumn = IniListArray[0].split(";").indexOf("code");	// detect code-column number
	var mainItemList = Registry.System.Logic.MainItem.split(";");	// get the main-unit list from Registry
	var order = <order><configurationtarget/><systemCode/><article/><foliant><Clicked/><Selection/><IconStatus/><Choices/></foliant></order>;	// initialise the order-XML-structure
	for ( var x=1; x<IniListArray.length; x++ )	// run over all list-lines
	{
		var itemName = IniListArray[x].split(";")[nameColumn]+"";	// get the list column for itemNames
		var itemCode = IniListArray[x].split(";")[codeColumn]+"";	// get the list column for itemCodes
		var idx = order.article.art.(@code == itemCode);	// get the article with @code == itemCode
		if ( idx.length() == 0 )	// if XML idx is empty (length==0)
		{	
			order.article.insertChildAfter( null, <art/> );	// create new article
			order.article.art[0].@code = itemCode;	// set child's @code to article-code
			order.article.art[0].@mainUnit = 0;	// set mainUnit-marker to default=0
			order.article.art[0].@quantity = 0;	// set @quantity
			order.article.art[0].@item = "";	// set @item-name as ";"-sep stringlist
			idx=0;	// set idx to first childIndex -> 0
		} 
		else idx = order.article.art.(@code == itemCode).childIndex();	// if idx not empty, set idx to childIndex
		if ( mainItemList.indexOf(itemName) >= 0 ) order.article.art[idx].@mainUnit = 1;	// set MainUnit-attribute if ...
		if ( isSelected( itemName ) == true ) order.article.art[idx].@quantity ++;	// set @quantity
		if ( ( mainItemList.indexOf(itemName) >= 0 ) &&  isSelected( itemName ) ) order.systemCode = itemCode;	// set systemCode if ...
		order.article.art[idx].@item = order.article.art[idx].@item+itemName+";";	// set @item-name as ";"-sep stringlist
	}
	order.foliant.Clicked = Registry.System.Logic.Clicked.toString();	// add the Foliant's clicked-list
	order.foliant.Selection = Registry.System.Logic.Selection.toString();	// add the Foliant's selection-list
	order.foliant.IconStatus = Registry.System.Logic.IconStatus.toString();	// add the Foliant's Icons and Items-list
	for each ( var x in Registry.System.Logic.FunctionGroups.Choice.children() )	// run over all Choice-entries ...
		order.foliant.Choices.insertChildBefore(null,XML( "<"+x.localName()+">"+x.@items.toString()+"</"+x.localName()+">" ));	// ... and save the choosen values
	order.foliant.idCode = Registry.Internals.ident.idCode.@value.toString();	// add the idCode of the specific Foliant
	order.foliant.language = Library.CurrentLanguage.@lcid.toString();	// save the current used language
	order.foliant.visiblePage = Library.VisiblePage.toString();	// save the current visible results-page
	
	order.foliant.targetURL = srvAddress;
	order.foliant.targetFileName = srvFileName;
	order.foliant.targetProtocol = srvProtocol;
	order.foliant.targetMessage = srvMessage;
	order.foliant.submitParam = "";
	for ( var sp in submitParam ) order.foliant.submitParam.insertChildBefore( null, XML("<"+sp+" value=\""+submitParam[sp]+"\"/>") );
	
	order.complete = Boolean([display.visible,display.hidden].indexOf(this.getField("Btn_IncompleteSys").display));	// save the validation-status
	return order;
}

var FoliantVersionMain = "1";
var FoliantVersionSub = "10";

var guid = "";	// web-browser URL parameter ( UID )

function _startup() 
{
	// activate the startup-overlay-screen 
	var f = this.getField("Btn_VersionCheckOverlay");
	f.delay = true;
	f.display = display.visible;
	f.rect = [0, 842, 595, 0 ];
	var msgVersionWarning = f.userName;
	f.delay = false;
	// check the current version of the PDF-viewer for compatibility
	var reqVersion = 8.0;
	var appInfo = {};
		appInfo.Type = app.viewerType; 
		appInfo.Variation = app.viewerVariation;
		appInfo.Version = app.viewerVersion;
		appInfo.VersionText = 'v'+appInfo.Version; if (appInfo.VersionText.indexOf(".")<0) appInfo.VersionText += '.0';
	if ( appInfo.Version < reqVersion )
	{
		var nButton = app.alert({cMsg: msgVersionWarning, cTitle: appInfo.Type+'  ( '+appInfo.Variation+' )  '+appInfo.VersionText, nIcon: 1, nType: 1 });
		if ( nButton == 2 ) this.closeDoc(); 
	}
} 

_startup();	// call the startup-function first - before XML-activities to protect from too old application versions
this.disclosed = true;	// make PDF detectable for other PDF-sessions
var markedDirty = this.dirty; // create variable to indicate the document's changes to be saved ( per function )
var backSelection = false;	// Indicator for "Back-Button" has been pressed
var ownerId = 0;	// set Owner indicator to default;

// initialise and check the Registry-XML-structure
registryInit();
if ( String(Registry.System.Atoms.toString()) =="") { Registry.System.Atoms=""; registrySave() }	// check for empty Atoms-structure, if empty, create "" value on it
if ( String(Registry.System.Logic.toString()) =="") { Registry.System.Logic=""; registrySave() }	// check for empty Logic-structure, if empty, create "" value on it

// define the registry internal version values from here
Registry.Internals.ident.@versionMain=FoliantVersionMain;
Registry.Internals.ident.@versionSub=FoliantVersionSub;

// initialise the Library-XML-Structure
Library_Init();

// call initialisation functions

// create the info-text for the status-bar ( BottomLine )
setBottomBarInfoLine();	

// set the submit-button to visible by default
Registry.Internals.menuItems.Btn_SubmitCRM.@visible=true;	

// is browser-detection enabled ??? If yes ...
if ( String(Registry.Internals.settings.detectExternal.@value) == "true" ) 	
	if ( ( this.external == false ) || ( guid == "" ) ) Registry.Internals.menuItems.Btn_SubmitCRM.@visible=false;	// ... and no browser or no guid parameter found, SubmitCRM-btn will be hidden

// set all menu buttons to Registry-defined values
arrangeMenuButtons();	

// switch off focus rectangles for buttons ( icons )
app.focusRect = false;	

// switch off highliting of edit fields
app.runtimeHighlight = false;	

// set "dirty"-value to the startup-value
this.dirty = markedDirty;

// save all re-initialised values in Registry-XML-variable
registrySave();

// remove the startup-overlay
if ( ownerId.toString() == "28653030" ) this.getField("Btn_VersionCheckOverlay").rect = [0, 0, 0, 0 ];

// identify the document by internal registry values / returns the ident-structure
var identifyDoc = Registry.Internals.ident;

// initialise transmission-system
transmitInit();

this.dirty = false;
markedDirty = this.dirty; // indicate, there where no document's changes to be saved




