// Format Document List
const items = $input.all();
const params = $('Execute Workflow Trigger').item.json;

if (items.length === 0) {
  return {
    found: false,
    message: `ðŸ“„ Keine ${params.document_type} Dokumente gefunden${params.manufacturer ? ' fÃ¼r ' + params.manufacturer : ''}.`
  };
}

// Document type labels
const typeLabels = {
  'service_bulletin': 'ðŸ“‹ Service Bulletin',
  'service_manual': 'ðŸ“• Service Manual',
  'parts_catalog': 'ðŸ”§ Parts Catalog',
  'user_manual': 'ðŸ“˜ User Manual'
};

const typeIcon = typeLabels[params.document_type] || 'ðŸ“„';

// Format file size
const formatSize = (bytes) => {
  if (!bytes) return 'N/A';
  const mb = bytes / (1024 * 1024);
  return mb >= 1 ? `${mb.toFixed(1)} MB` : `${(bytes / 1024).toFixed(0)} KB`;
};

// Format date
const formatDate = (dateStr) => {
  return new Date(dateStr).toLocaleDateString('de-DE', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric'
  });
};

// Build response
let response = `${typeIcon} GEFUNDENE DOKUMENTE\n`;
response += `Filter: ${params.document_type}${params.manufacturer ? ' | ' + params.manufacturer : ''}\n\n`;

items.forEach((item, index) => {
  const doc = item.json;
  response += `${index + 1}. ${doc.filename}\n`;

  if (doc.manufacturer) response += `   ðŸ­ ${doc.manufacturer}`;
  if (doc.series) response += ` - ${doc.series}`;
  if (doc.models && doc.models.length > 0) response += ` (${doc.models.join(', ')})`;
  response += `\n`;

  if (doc.version) response += `   ðŸ“Œ Version: ${doc.version}\n`;
  response += `   ðŸ“… Datum: ${formatDate(doc.created_at)}\n`;
  response += `   ðŸ’¾ GrÃ¶ÃŸe: ${formatSize(doc.file_size)}\n`;

  if (doc.chunk_count || doc.image_count || doc.error_code_count) {
    response += `   ðŸ“Š Inhalt: `;
    const parts = [];
    if (doc.chunk_count) parts.push(`${doc.chunk_count} Chunks`);
    if (doc.image_count) parts.push(`${doc.image_count} Bilder`);
    if (doc.error_code_count) parts.push(`${doc.error_code_count} Error Codes`);
    response += parts.join(', ') + `\n`;
  }

  response += `\n`;
});

response += `\nâœ… Gesamt: ${items.length} ${params.document_type} Dokument${items.length > 1 ? 'e' : ''}`;

return {
  found: true,
  count: items.length,
  document_type: params.document_type,
  formatted_response: response,
  documents: items.map(i => i.json)
};
