parameters.jsCode:
// Format Error Code Response
const item = $input.first().json;

if (!item || !item.error_code) {
  return {
    found: false,
    message: `🔴 Error Code nicht gefunden: ${$('Execute Workflow Trigger').item.json.error_code}\n\n❌ Dieser Error Code ist nicht in der Datenbank.\n\n💡 Versuche:\n- Prüfe die Schreibweise (z.B. C-2801 vs C2801)\n- Nutze Vector Search für ähnliche Probleme\n- Beschreibe das Problem direkt`
  };
}

// Format solution text with proper line breaks
const formatSolution = (text) => {
  if (!text) return 'Keine Lösung verfügbar';
  return text.split('\n').map((line, i) => {
    if (line.match(/^\d+\./)) return line;  // Already numbered
    return `${i + 1}. ${line.trim()}`;
  }).join('\n');
};

// Build formatted response
let response = `🔴 ERROR CODE: ${item.error_code}\n\n`;

// Description
response += `📝 BESCHREIBUNG:\n${item.error_description || 'Keine Beschreibung verfügbar'}\n\n`;

// Solution
if (item.solution_text) {
  response += `🔧 LÖSUNG:\n${formatSolution(item.solution_text)}\n\n`;
}

// Context from manual
if (item.context_text) {
  const contextPreview = item.context_text.substring(0, 300);
  response += `📄 CONTEXT AUS MANUAL:\n${contextPreview}${item.context_text.length > 300 ? '...' : ''}\n\n`;
}

// Screenshot
if (item.screenshot_url) {
  response += `🖼️ SCREENSHOT:\n${item.screenshot_url}\n`;
  if (item.screenshot_description) {
    response += `   ${item.screenshot_description}\n`;
  }
  if (item.match_method) {
    response += `   ✨ Match-Methode: ${item.match_method}\n`;
  }
  response += `\n`;
}

// Source information
response += `ℹ️ DETAILS:\n`;
if (item.document_name) response += `  📄 Dokument: ${item.document_name}\n`;
if (item.manufacturer) response += `  🏭 Hersteller: ${item.manufacturer}\n`;
if (item.series) response += `  📦 Serie: ${item.series}\n`;
if (item.page_number) response += `  📖 Seite: ${item.page_number}\n`;
if (item.severity_level) response += `  ⚠️ Schweregrad: ${item.severity_level}\n`;
if (item.requires_technician !== null) {
  response += `  👨‍🔧 Techniker: ${item.requires_technician ? 'Erforderlich' : 'Nicht erforderlich'}\n`;
}
if (item.confidence_score) {
  const confidence = Math.round(item.confidence_score * 100);
  response += `  📊 Konfidenz: ${confidence}%\n`;
}

return {
  found: true,
  error_code: item.error_code,
  formatted_response: response,
  raw_data: item
};
