// Try multiple input sources
let input = {};

try {
  // Method 1: From $input (workflow execution)
  if ($input && $input.first && $input.first()) {
    input = $input.first().json || {};
  }
} catch (e) {
  console.log('No $input available');
}

// Method 2: From $json (direct node input)
if (Object.keys(input).length === 0 && typeof $json !== 'undefined') {
  input = $json;
}

// Method 3: From workflow parameters
if (Object.keys(input).length === 0 && typeof $parameter !== 'undefined') {
  input = $parameter;
}

// Debug: Log what we got
console.log('Input received:', JSON.stringify(input));
console.log('Input keys:', Object.keys(input));

// Extract error code from multiple possible fields
const errorCode = input.error_code || input.code || input.errorCode || '';
const manufacturer = input.manufacturer || input.mfr || input.brand || null;
const product = input.product || input.model || input.product_name || null;

// If still no error code, try to extract from text
if (!errorCode && input.text) {
  const match = input.text.match(/\b(\d{2}\.\d{2,3}\.\d{2})\b/);
  if (match) {
    return {
      error_code: match[1],
      manufacturer: manufacturer,
      product: product
    };
  }
}

if (!errorCode) {
  throw new Error('No error code provided! Input was: ' + JSON.stringify(input) + '. Expected: {"error_code": "30.03.30", "manufacturer": "HP", "product": "X580"}');
}

return {
  error_code: errorCode,
  manufacturer: manufacturer,
  product: product
};

const results = $input.first().json;

if (!results || results.length === 0) {
  return {
    success: false,
    message: `âŒ Error Code nicht gefunden!\n\nBitte prÃ¼fe:\n- Ist der Code korrekt? (z.B. 30.03.30)\n- Ist der Hersteller korrekt?\n- Ist das Modell korrekt?`
  };
}

// Group by source type
const documents = results.filter(r => r.source_type === 'document');
const videos = results.filter(r => r.source_type === 'video');
const relatedVideos = results.filter(r => r.source_type === 'related_video');

let message = `ðŸ”§ ERROR CODE: ${results[0].error_code}\n`;
if (results[0].error_description) {
  message += `ðŸ“ ${results[0].error_description}\n`;
}
message += '\n';

// Documents
if (documents.length > 0) {
  message += `ðŸ“– DOKUMENTATION (${documents.length}):`;\n\n`;

  documents.forEach((doc, i) => {
    message += `\n${i+1}. ${doc.source_title}`;
    if (doc.page_number) message += ` (Seite ${doc.page_number})`;
    message += '\n';

    if (doc.solution_text) {
      const solution = doc.solution_text.substring(0, 150);
      message += `   ðŸ’¡ ${solution}${doc.solution_text.length > 150 ? '...' : ''}\n`;
    }

    if (doc.parts_list) {
      message += `   ðŸ”§ Parts: ${doc.parts_list}\n`;
    }
  });
}

// Direct video matches
if (videos.length > 0) {
  message += `\n\nðŸŽ¬ VIDEOS (${videos.length}):`;\n\n`;

  videos.forEach((vid, i) => {
    message += `\n${i+1}. ${vid.source_title}`;
    if (vid.video_duration) {
      const mins = Math.floor(vid.video_duration / 60);
      const secs = vid.video_duration % 60;
      message += ` (${mins}:${String(secs).padStart(2, '0')})`;
    }
    message += '\n';

    if (vid.solution_text) {
      const solution = vid.solution_text.substring(0, 100);
      message += `   ${solution}${vid.solution_text.length > 100 ? '...' : ''}\n`;
    }

    if (vid.video_url) {
      message += `   ðŸ”— ${vid.video_url}\n`;
    }
  });
}

// Related videos
if (relatedVideos.length > 0) {
  message += `\n\nðŸ“º VERWANDTE VIDEOS (${relatedVideos.length}):`;\n\n`;

  relatedVideos.slice(0, 3).forEach((vid, i) => {
    message += `\n${i+1}. ${vid.source_title}`;
    if (vid.video_duration) {
      const mins = Math.floor(vid.video_duration / 60);
      const secs = vid.video_duration % 60;
      message += ` (${mins}:${String(secs).padStart(2, '0')})`;
    }
    message += '\n';

    if (vid.video_url) {
      message += `   ðŸ”— ${vid.video_url}\n`;
    }
  });

  if (relatedVideos.length > 3) {
    message += `\n   ... und ${relatedVideos.length - 3} weitere Videos\n`;
  }
}

message += '\n\nðŸ’¡ MÃ¶chtest du mehr Details zu einem der Quellen?';

return {
  success: true,
  message: message,
  raw_data: {
    documents: documents,
    videos: videos,
    related_videos: relatedVideos,
    total_sources: results.length
  }
};
