// Extract error code from user input
// Handles: "HP E877 Error 31.13.01", "31.13.01", "Error code 31.13.01", etc.

const input = $input.first().json.error_code || '';

// Common error code patterns
const patterns = [
  // HP format: XX.XX.XX or XX.XX
  /\b(\d{2}\.\d{2}\.\d{2})\b/,
  /\b(\d{2}\.\d{2})\b/,
  // Konica Minolta: C####, J##-##
  /\b([CJ]\d{4,5})\b/i,
  /\b([CJ]\d{2}-\d{2})\b/i,
  // Canon: E### or ####
  /\b(E\d{3,4})\b/i,
  /\b(#\d{3})\b/,
  // Ricoh: SC###
  /\b(SC\d{3,4})\b/i,
  // Brother: ## or E##
  /\b(E\d{2})\b/i,
  // Generic: Any number pattern
  /\b(\d{2,4})\b/
];

let extractedCode = null;

// Try each pattern
for (const pattern of patterns) {
  const match = input.match(pattern);
  if (match) {
    extractedCode = match[1];
    break;
  }
}

// If no pattern matched, use original input
if (!extractedCode) {
  extractedCode = input.trim();
}

return {
  error_code: extractedCode,
  original_input: input
};

