// Format Video Enrichment Response
const response = $input.first().json;

if (response.error) {
  return {
    success: false,
    message: `âŒ Video konnte nicht analysiert werden:\n${response.error}\n\nğŸ’¡ UnterstÃ¼tzte Plattformen:\n- YouTube (youtube.com, youtu.be)\n- Vimeo (vimeo.com)\n- Brightcove (players.brightcove.net)`
  };
}

// Format duration
const formatDuration = (seconds) => {
  if (!seconds) return 'N/A';
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;

  if (hours > 0) {
    return `${hours}:${String(minutes).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  }
  return `${minutes}:${String(secs).padStart(2, '0')}`;
};

// Format numbers
const formatNumber = (num) => {
  if (!num) return 'N/A';
  return num.toLocaleString('de-DE');
};

