// Format System Status for LLM
const data = $input.first().json;

// Calculate percentages
const errorCodePercentage = data.total_error_codes > 0 
  ? Math.round((data.error_codes_with_screenshots / data.total_error_codes) * 100)
  : 0;

const linkHealthPercentage = data.total_links > 0
  ? Math.round((data.active_links / data.total_links) * 100)
  : 0;

const completionPercentage = data.total_documents > 0
  ? Math.round((data.completed_documents / data.total_documents) * 100)
  : 0;

// Format dates
const formatDate = (dateStr) => {
  if (!dateStr) return 'Keine Daten';
  return new Date(dateStr).toLocaleString('de-DE', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
};

