  â”œâ”€ Videos: ${data.total_videos || 0}
  â”œâ”€ Links: ${data.total_links || 0} (${linkHealthPercentage}% aktiv)
  â””â”€ Error Codes: ${data.total_error_codes || 0} (${errorCodePercentage}% mit Screenshots)

ğŸ­ KATALOG:
  â”œâ”€ Produkte: ${(data.total_products || 0).toLocaleString('de-DE')}
  â””â”€ Hersteller: ${data.total_manufacturers || 0}

â±ï¸ LETZTE AKTIVITÃ„TEN:
  â”œâ”€ Dokument: ${formatDate(data.last_document_added)}
  â”œâ”€ Video: ${formatDate(data.last_video_added)}
  â””â”€ Error Code: ${formatDate(data.last_error_code_added)}

âœ… System: Betriebsbereit`;

return {
  status_text: statusText,
  raw_data: data,
  summary: {
    total_documents: data.total_documents || 0,
    total_content_items: (data.total_chunks || 0) + (data.total_images || 0) + (data.total_videos || 0),
    error_codes_with_screenshots_pct: errorCodePercentage,
