const parts = text.trim().split(/\s+/);
const command = parts[0].substring(1).toLowerCase();
const args = parts.slice(1);

return {
  command: command,
  args: args,
  text: text,
  user: $input.item.json.user,
  conversationId: $input.item.json.conversationId,
  channelId: $input.item.json.channelId
};

// Prepare prompt for Ollama
const text = $input.item.json.text;

const prompt = `Du bist ein Intent-Detector fÃ¼r einen technischen Support-Bot.

User Input: ${text}

Analysiere die Anfrage und gib NUR ein JSON zurÃ¼ck (keine ErklÃ¤rung!):

{
  "intent": "ERROR_CODE" | "DEVICE_STATUS" | "PRODUCT_INFO" | "HELP" | "UNKNOWN",
  "manufacturer": "HP" | "Konica Minolta" | "Canon" | etc. (falls erkennbar),
  "model": "X580" | "M455" | etc. (falls erkennbar),
  "errorCode": "12.34.56" | "C2345" | etc. (falls erkennbar),
