}

Beispiele:
- "HP X580 Fehler 12.34.56" â†’ {"intent":"ERROR_CODE","manufacturer":"HP","model":"X580","errorCode":"12.34.56"}
- "Trommel Restlaufzeit GerÃ¤t 12345" â†’ {"intent":"DEVICE_STATUS","deviceId":"12345"}
- "Kann X580 Duplexscan?" â†’ {"intent":"PRODUCT_INFO","model":"X580"}
- "Was kannst du?" â†’ {"intent":"HELP"}`;

return {
  prompt: prompt,
