// Call Ollama API directly
const prompt = $input.item.json.prompt;

const response = await fetch('http://host.docker.internal:11434/api/generate', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    model: 'qwen2.5:7b',
    prompt: prompt,
    stream: false
  })
});

const data = await response.json();

return {
  response: data.response,
  model: data.model,
  done: data.done,
  ...($input.item.json)
};

// Parse Ollama response
const ollamaResponse = $input.item.json.response || $input.item.json.text || '{}';
const previousData = $('Prepare Ollama Prompt').item.json;

let parsed = {};
try {
  // Try to extract JSON from response
  const jsonMatch = ollamaResponse.match(/\{[^}]+\}/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  // Fallback to simple pattern matching
  const text = previousData.text.toLowerCase();

  if (text.match(/fehler|error|code/i)) {
    parsed.intent = 'ERROR_CODE';
    const codeMatch = text.match(/\b([0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}|[A-Z]?[0-9]{4,6})\b/);
    if (codeMatch) parsed.errorCode = codeMatch[0];

    if (text.match(/\bhp\b|hewlett/i)) parsed.manufacturer = 'Hewlett Packard';
    else if (text.match(/konica|minolta/i)) parsed.manufacturer = 'Konica Minolta';
    else if (text.match(/canon/i)) parsed.manufacturer = 'Canon';
  }
  else if (text.match(/help|hilfe|commands/i)) {
    parsed.intent = 'HELP';
  }
  else {
    parsed.intent = 'UNKNOWN';
  }
}

return {
  intent: parsed.intent || 'UNKNOWN',
  manufacturer: parsed.manufacturer,
  model: parsed.model,
  errorCode: parsed.errorCode,
  deviceId: parsed.deviceId,
  text: previousData.text,
  user: previousData.user,
  conversationId: previousData.conversationId,
  channelId: previousData.channelId
};

