// Format product info results for the agent
const results = $input.all();

if (results.length === 0) {
  return [{
    json: {
      message: "Keine Produkt-Informationen gefunden.",
      results: []
    }
  }];
}

// Format each result
const formatted = results.map(item => ({
  product_id: item.json.product_id,
  model_number: item.json.model_number,
  manufacturer: item.json.manufacturer,
  series_name: item.json.series_name,
  product_type: item.json.product_type,
  oem_manufacturer: item.json.oem_manufacturer,
  oem_relationship: item.json.oem_relationship_type,
  document_count: item.json.document_count
}));

return [{
  json: {
    message: `${results.length} Produkt(e) gefunden`,
    results: formatted
  }
}];
